succes = 0
losses = 0

def choise(move):
    if move == 1:
        print('rockü•å')
    elif move == 2:
        print('paperüßª')
    elif move == 3:
        print('scisser‚úÇ')
    else:
        print('you did not choose any number')

while succes < 3 and losses < 3:
    pc = randint(1, 3)
    player = int(input('chose: '))
    print('you chose'),  choise(player)
    print('pc chose'), choise(pc)
    if pc == player:
        print('tie')
    elif (player == 1 and pc == 3):
        print('you win')
        succes += 1
    elif (player == 2 and pc == 1):
        print('you win')
        succes += 1
    elif (player == 3 and pc == 2):
        print('you win')
        succes += 1
    else:
        print('you lose')
        losses += 1
    print('-----------------------------------------------------')

